// src/pages/DashboardPage.jsx
import React, { useState, useEffect } from 'react';
import { Doughnut } from 'react-chartjs-2';

// Chart.js kurarken gerekebilecek register'lar (eğer ChartJS v3+ kullanıyorsanız)
import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend
} from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend);

function DashboardPage() {
  const [cpuUsage, setCpuUsage] = useState(35);   // başlangıçta %35

  // Rastgele CPU değeri simülasyonu
  useEffect(() => {
    const interval = setInterval(() => {
      const randomVal = Math.floor(Math.random() * 100);
      setCpuUsage(randomVal);
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  // Doughnut chart verisi
  // İki dilimli grafik: birincisi "kullanılan", ikincisi "kalan"
  const data = {
    labels: ['Kullanılan', 'Boş'],
    datasets: [
      {
        data: [cpuUsage, 100 - cpuUsage],
        backgroundColor: [
          'rgba(54, 162, 235, 0.8)',  // Mavi ton, kullanılan
          'rgba(201, 203, 207, 0.3)'  // Gri ton, boş
        ],
        borderWidth: 1,
      },
    ],
  };

  const options = {
    responsive: true,
    cutout: '60%',       // doughnut'un ortasındaki boşluk genişliği
    plugins: {
      legend: {
        display: false   // İsterseniz legend'ı kapatabilir veya özelleştirebilirsiniz
      },
      tooltip: {
        // tooltip ayarları
      },
    },
  };

  return (
    <div className="flex h-screen bg-gray-900">
      <aside className="sidebar w-64 flex flex-col justify-between p-4">
        {/* Sidebar header */}
        <div>
          <div className="flex items-center justify-between mb-8">
            <div className="flex items-center">
              <span className="material-icons text-3xl text-blue-500 mr-2">shield</span>
              <h1 className="text-xl font-bold text-white">NextDNS</h1>
            </div>
            <button className="icon-button"><span className="material-icons">add</span></button>
          </div>
          {/* User profile */}
          <div className="flex items-center mb-6 p-3 rounded-md hover:bg-gray-700 cursor-pointer">
            <img src="" alt="User profile" className="w-8 h-8 rounded-full mr-3"/>
            <p className="text-sm font-medium text-white">My Profile</p>
            <span className="material-icons ml-auto text-gray-400">expand_more</span>
          </div>
          {/* Nav links */}
          <nav>
            <ul>
              <li><a className="nav-item active" href="#"><span className="material-icons">dashboard</span>Dashboard</a></li>
              {/* ...other nav items... */}
            </ul>
          </nav>
        </div>
        {/* Footer with plan info */}
        <div className="p-3 bg-gray-900 rounded-md">
          <div className="flex justify-between text-xs mb-1 text-gray-400"><span>Free Plan</span><span>300K queries/mo</span></div>
          <div className="progress-bar mb-1"><div className="progress-bar-inner" style={{width: '20%'}}/></div>
          <p className="text-xs text-gray-400">7 / 300,000 queries used</p>
        </div>
      </aside>
      <main className="flex-1 p-8 overflow-y-auto">
        <header className="mb-8">
          <h2 className="text-3xl font-semibold text-white">Dashboard</h2>
