// src/theme.js
import { createTheme } from '@mui/material/styles';

export const lightTheme = createTheme({
  palette: {
    mode: 'light',
    primary: {
      main: '#1976d2', // Açık tema için mavi tonu
    },
    secondary: {
      main: '#dc004e', // Açık tema için pembe/kırmızı tonu
    },
    background: {
      default: '#f4f6f8',
      paper: '#ffffff',
    },
  },
  typography: {
    fontFamily: 'Roboto, Arial, sans-serif',
  },
});

export const darkTheme = createTheme({
  palette: {
    mode: 'dark', // Dark mode
    primary: {
      main: '#0288d1', // Mavi ton
    },
    secondary: {
      main: '#cddc39', // Yeşilimsi
    },
    background: {
      default: '#121212', // arka plan
      paper: '#1d1d1d', // Card vb.
    },
  },
  typography: {
    fontFamily: 'Roboto, Arial, sans-serif',
  },
});

// Varsayılan olarak koyu temayı kullan
const getTheme = (mode) => (mode === 'dark' ? darkTheme : lightTheme);

export default getTheme;
