// File: src/pages/FirewallGroupsPage.jsx

import React, { useEffect, useState } from "react";
import axios from "axios";

function FirewallGroupsPage() {
  const [groups, setGroups] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [isEditMode, setIsEditMode] = useState(false);

  // Form alanları
  const [groupId, setGroupId] = useState(null);
  const [groupName, setGroupName] = useState("");
  const [description, setDescription] = useState("");

  useEffect(() => {
    fetchGroups();
  }, []);

  const fetchGroups = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://127.0.0.1:8000/firewall/groups", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setGroups(res.data);
    } catch (err) {
      alert("Grup listesi alınamadı: " + err.message);
    }
  };

  const clearForm = () => {
    setGroupId(null);
    setGroupName("");
    setDescription("");
  };

  const handleNewGroup = () => {
    clearForm();
    setIsEditMode(false);
    setShowForm(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");

    const body = {
      group_name: groupName,
      description: description,
    };

    try {
      if (!isEditMode) {
        // POST -> grup ekle
        const res = await axios.post("http://127.0.0.1:8000/firewall/groups", body, {
          headers: { Authorization: `Bearer ${token}` },
        });
        alert(res.data.message);
      } else {
        // PUT -> güncelle
        const url = `http://127.0.0.1:8000/firewall/groups/${groupId}`;
        const res = await axios.put(url, body, {
          headers: { Authorization: `Bearer ${token}` },
        });
        alert(res.data.message);
      }
      setShowForm(false);
      fetchGroups();
    } catch (err) {
      alert("Grup kaydedilemedi: " + (err.response?.data?.detail || err.message));
    }
  };

  const handleEdit = (g) => {
    setIsEditMode(true);
    setShowForm(true);
    setGroupId(g._id);
    setGroupName(g.group_name || "");
    setDescription(g.description || "");
  };

  const handleDelete = async (g) => {
    if (!window.confirm(`'${g.group_name}' grubunu silmek istediğinize emin misiniz?`)) return;
    try {
      const token = localStorage.getItem("token");
      const url = `http://127.0.0.1:8000/firewall/groups/${g._id}`;
      const res = await axios.delete(url, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert(res.data.message);
      fetchGroups();
    } catch (err) {
      alert("Silme hatası: " + (err.response?.data?.detail || err.message));
    }
  };

  return (
    <div className="container mx-auto p-4">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-semibold text-gray-700">Kural Grupları</h1>
        <button
          onClick={handleNewGroup}
          className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded shadow-md transition duration-150 ease-in-out"
        >
          <span className="material-icons mr-2">add</span>Yeni Grup Ekle
        </button>
      </div>

      {showForm && (
        <div className="bg-white shadow-xl rounded-lg p-6 mb-8">
          <h2 className="text-xl font-semibold text-gray-700 mb-4">
            {isEditMode ? "Grubu Düzenle" : "Yeni Grup Oluştur"}
          </h2>
          <form onSubmit={handleSubmit}>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label htmlFor="groupName" className="block text-sm font-medium text-gray-700 mb-1">
                  Grup Adı
                </label>
                <input
                  type="text"
                  id="groupName"
                  value={groupName}
                  onChange={(e) => setGroupName(e.target.value)}
                  className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                  required
                />
              </div>
              <div>
                <label htmlFor="description" className="block text-sm font-medium text-gray-700 mb-1">
                  Açıklama
                </label>
                <input
                  type="text"
                  id="description"
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                  className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                />
              </div>
            </div>
            <div className="flex justify-end mt-6 space-x-3">
              <button
                type="button"
                onClick={() => setShowForm(false)}
                className="bg-gray-200 hover:bg-gray-300 text-gray-700 font-semibold py-2 px-4 rounded shadow-md transition duration-150 ease-in-out"
              >
                İptal
              </button>
              <button
                type="submit"
                className="bg-green-500 hover:bg-green-600 text-white font-semibold py-2 px-4 rounded shadow-md transition duration-150 ease-in-out"
              >
                {isEditMode ? "Güncelle" : "Kaydet"}
              </button>
            </div>
          </form>
        </div>
      )}

      <div className="bg-white shadow-xl rounded-lg overflow-hidden">
        <table className="min-w-full leading-normal">
          <thead>
            <tr className="bg-gray-100 text-left text-gray-600 uppercase text-sm leading-normal">
              <th className="py-3 px-6">Grup Adı</th>
              <th className="py-3 px-6">Açıklama</th>
              <th className="py-3 px-6 text-center">Eylemler</th>
            </tr>
          </thead>
          <tbody className="text-gray-700 text-sm font-light">
            {groups.length > 0 ? (
              groups.map((group) => (
                <tr key={group._id} className="border-b border-gray-200 hover:bg-gray-50">
                  <td className="py-3 px-6 whitespace-nowrap">
                    <div className="flex items-center">
                      <span className="font-medium">{group.group_name}</span>
                    </div>
                  </td>
                  <td className="py-3 px-6">
                    {group.description || "-"}
                  </td>
                  <td className="py-3 px-6 text-center">
                    <div className="flex item-center justify-center space-x-2">
                      <button
                        onClick={() => handleEdit(group)}
                        className="text-blue-500 hover:text-blue-700 transition duration-150 ease-in-out"
                        title="Düzenle"
                      >
                        <span className="material-icons">edit</span>
                      </button>
                      <button
                        onClick={() => handleDelete(group)}
                        className="text-red-500 hover:text-red-700 transition duration-150 ease-in-out"
                        title="Sil"
                      >
                        <span className="material-icons">delete</span>
                      </button>
                    </div>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="3" className="py-6 px-6 text-center text-gray-500">
                  Henüz kural grubu oluşturulmamış.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default FirewallGroupsPage;
