import React, { useEffect, useRef } from 'react';
import { Chart, registerables } from 'chart.js';

// Chart.js'in tüm bileşenlerini kaydet
Chart.register(...registerables);

// Bağımsız bileşenler
const Icon = ({ name }) => <span className="material-icons">{name}</span>;

const NavItem = ({ icon, label, active = false }) => (
    <li>
        <a href="#" className={`nav-item ${active ? 'active' : ''}`}>
            <Icon name={icon} />
            {label}
        </a>
    </li>
);

const InfoCard = ({ title, value, description, icon, iconClass }) => (
    <div className="card">
        <div className="flex justify-between items-center mb-2">
            <h3 className="text-sm font-medium text-gray-400">{title}</h3>
            <Icon name={icon} />
        </div>
        <p className="text-2xl font-semibold text-white">{value}</p>
        <p className="text-xs text-gray-400">{description}</p>
    </div>
);

const StatCard = ({ title, value, subtext, icon, iconClass }) => (
    <div className="card">
        <div className="flex justify-between items-center mb-1">
            <h3 className="text-sm font-medium text-gray-400">{title}</h3>
            {icon && <Icon name={icon} />}
        </div>
        <p className="text-3xl font-bold text-white">{value}</p>
        <p className="text-xs text-gray-400">{subtext}</p>
    </div>
);

const ActivityItem = ({ domain, client, ip, status, time }) => {
    const getBadgeClass = (status) => {
        switch (status) {
            case 'Blocked': return 'badge-red';
            case 'Allowed': return 'badge-green';
            case 'Cached': return 'badge-gray';
            default: return 'badge-gray';
        }
    };

    return (
        <li className="flex justify-between items-center">
            <div>
                <p className="text-sm font-medium text-white">{domain}</p>
                <p className="text-xs text-gray-400">{client} <span className="text-gray-500">{ip}</span></p>
            </div>
            <span className={`badge ${getBadgeClass(status)}`}>{status}</span>
            <span className="text-xs text-gray-500">{time}</span>
        </li>
    );
};


const AnalyticsChart = () => {
    const chartRef = useRef(null);

    useEffect(() => {
        const ctx = chartRef.current.getContext('2d');
        const analyticsChart = new Chart(ctx, {
            type: 'line',
            data: {
                labels: ['00:00', '02:00', '04:00', '06:00', '08:00', '10:00', '12:00', '14:00', '16:00', '18:00', '20:00', '22:00'],
                datasets: [{
                    label: 'Total Queries',
                    data: [200, 180, 150, 160, 250, 500, 700, 750, 680, 600, 500, 400],
                    borderColor: 'rgb(59, 130, 246)', // blue-500
                    backgroundColor: 'rgba(59, 130, 246, 0.1)',
                    tension: 0.4,
                    borderWidth: 2,
                    pointRadius: 0,
                    pointHoverRadius: 5,
                    fill: true,
                }, {
                    label: 'Blocked',
                    data: [50, 40, 30, 35, 60, 120, 180, 200, 150, 130, 100, 80],
                    borderColor: 'rgb(239, 68, 68)', // red-500
                    backgroundColor: 'rgba(239, 68, 68, 0.1)',
                    tension: 0.4,
                    borderWidth: 2,
                    pointRadius: 0,
                    pointHoverRadius: 5,
                    fill: true,
                }]
            },
            options: {
                responsive: true,
                maintainAspectRatio: false,
                scales: {
                    y: {
                        beginAtZero: true,
                        ticks: {
                            color: '#9CA3AF', // text-gray-400
                            stepSize: 250,
                        },
                        grid: {
                            color: '#374151', // bg-gray-700
                            drawBorder: false,
                        }
                    },
                    x: {
                        ticks: {
                            color: '#9CA3AF' // text-gray-400
                        },
                        grid: {
                            display: false,
                        }
                    }
                },
                plugins: {
                    legend: {
                        display: false
                    },
                    tooltip: {
                        mode: 'index',
                        intersect: false,
                        backgroundColor: '#1F2937', // bg-gray-800
                        titleColor: '#FFFFFF',
                        bodyColor: '#D1D5DB',
                        borderColor: '#374151',
                        borderWidth: 1,
                        padding: 10,
                        cornerRadius: 4,
                    }
                }
            }
        });

        // Cleanup chart on component unmount
        return () => analyticsChart.destroy();
    }, []);

    return (
       <div className="lg:col-span-2 card">
            <div className="flex justify-between items-center mb-4">
                <h3 className="text-lg font-semibold text-white">Analytics</h3>
                <div className="flex space-x-1">
                    <button className="px-3 py-1 text-xs font-medium text-gray-300 bg-gray-700 rounded-md hover:bg-gray-600">24h</button>
                    <button className="px-3 py-1 text-xs font-medium text-gray-300 bg-gray-700 rounded-md hover:bg-gray-600">7d</button>
                    <button className="px-3 py-1 text-xs font-medium text-gray-300 bg-gray-700 rounded-md hover:bg-gray-600">30d</button>
                </div>
            </div>
            <p className="text-sm text-gray-400 mb-4">DNS query activity over time</p>
            <div className="h-80">
                <canvas ref={chartRef}></canvas>
            </div>
            <div className="flex justify-center space-x-6 mt-4 text-sm">
                <div className="flex items-center">
                    <span className="w-3 h-3 rounded-full bg-blue-500 mr-2"></span>
                    <span className="text-gray-400">Total Queries</span>
                </div>
                <div className="flex items-center">
                    <span className="w-3 h-3 rounded-full bg-red-500 mr-2"></span>
                    <span className="text-gray-400">Blocked</span>
                </div>
            </div>
        </div>
    );
};


function DashboardPage() {
    // Statik veriler
    const recentActivities = [
        { domain: 'analytics.google.com', client: 'Chrome', ip: '192.168.1.1', status: 'Blocked', time: '2m ago' },
        { domain: 'github.com', client: 'Firefox', ip: '192.168.1.5', status: 'Allowed', time: '5m ago' },
        { domain: 'ads.doubleclick.net', client: 'Safari', ip: '192.168.1.10', status: 'Blocked', time: '9m ago' },
        { domain: 'vercel.com', client: 'Chrome', ip: '192.168.1.1', status: 'Allowed', time: '12m ago' },
        { domain: 'api.segment.io', client: 'Firefox', ip: '192.168.1.5', status: 'Cached', time: '15m ago' },
    ];

    // Google fontlarını ve stilleri eklemek için
    useEffect(() => {
        const style = document.createElement('style');
        style.innerHTML = `
            @import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&display=swap');
            @import url('https://fonts.googleapis.com/icon?family=Material+Icons');
            body { font-family: 'Inter', sans-serif; background-color: #111827; color: #D1D5DB; }
            .sidebar { background-color: #1F2937; }
            .card { background-color: #1F2937; border-radius: 0.5rem; padding: 1.5rem; }
            .nav-item { display: flex; align-items: center; padding: 0.75rem 1.25rem; border-radius: 0.375rem; transition: background-color 0.2s ease-in-out; }
            .nav-item:hover, .nav-item.active { background-color: #374151; }
            .nav-item.active { color: #FFFFFF; }
            .nav-item .material-icons { margin-right: 0.75rem; font-size: 1.25rem; }
            .progress-bar { background-color: #374151; border-radius: 9999px; height: 0.5rem; overflow: hidden; }
            .progress-bar-inner { background-color: #EF4444; height: 100%; }
            .badge { padding: 0.25rem 0.5rem; border-radius: 0.25rem; font-size: 0.75rem; font-weight: 500; }
            .badge-green { background-color: #10B981; color: #FFFFFF; }
            .badge-red { background-color: #EF4444; color: #FFFFFF; }
            .badge-gray { background-color: #4B5563; color: #D1D5DB; }
            .icon-button { color: #9CA3AF; transition: color 0.2s ease-in-out; }
            .icon-button:hover { color: #FFFFFF; }
        `;
        document.head.appendChild(style);
        return () => {
            document.head.removeChild(style);
        };
    }, []);


    return (
        <div className="flex h-screen bg-gray-900 text-gray-300">
            {/* Sidebar */}
            <aside className="sidebar w-64 flex flex-col justify-between p-4">
                <div>
                    <div className="flex items-center justify-between mb-8">
                        <div className="flex items-center">
                            <Icon name="shield" />
                            <h1 className="text-xl font-bold text-white ml-2">NextDNS</h1>
                        </div>
                        <button className="icon-button"><Icon name="add" /></button>
                    </div>

                    <div className="flex items-center mb-6 p-3 rounded-md hover:bg-gray-700 cursor-pointer">
                        <img src="https://placehold.co/32x32/7e22ce/ffffff?text=U" alt="User profile" className="w-8 h-8 rounded-full mr-3"/>
                        <div>
                            <p className="text-sm font-medium text-white">My Profile</p>
                        </div>
                        <Icon name="expand_more" />
                    </div>

                    <nav>
                        <ul>
                            <NavItem icon="dashboard" label="Dashboard" active={true} />
                            <NavItem icon="bar_chart" label="Logs" />
                            <NavItem icon="settings" label="Settings" />
                            <NavItem icon="security" label="Security" />
                            <NavItem icon="lock_open" label="Privacy" />
                            <NavItem icon="family_restroom" label="Parental Control" />
                            <NavItem icon="list_alt" label="Denylist" />
                            <NavItem icon="playlist_add_check" label="Allowlist" />
                            <NavItem icon="help_outline" label="Help" />
                        </ul>
                    </nav>
                </div>

                <div className="p-3 bg-gray-900 rounded-md">
                    <div className="flex justify-between items-center text-xs mb-1">
                        <span className="text-gray-400">Free Plan</span>
                        <span className="text-gray-400">300K queries/mo</span>
                    </div>
                    <div className="progress-bar mb-1">
                        <div className="progress-bar-inner" style={{ width: '20%' }}></div>
                    </div>
                    <p className="text-xs text-gray-400">7 / 300,000 queries used</p>
                </div>
            </aside>

            {/* Main Content */}
            <main className="flex-1 p-8 overflow-y-auto">
                <header className="mb-8">
                    <h2 className="text-3xl font-semibold text-white">Dashboard</h2>
                    <p className="text-gray-400">Overview of your NextDNS configuration and activity.</p>
                </header>

                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
                    <InfoCard title="Status" value="Active" description="Your DNS configuration is working properly" icon="check_circle" iconClass="text-green-500" />
                    <InfoCard title="Connected Devices" value="5" description="Devices using your NextDNS configuration" icon="devices" iconClass="text-blue-500" />
                    <InfoCard title="Endpoints" value="2" description="Active DNS endpoints in use" icon="dns" iconClass="text-yellow-500" />
                    <InfoCard title="Last Update" value="2h ago" description="Last configuration change" icon="history" iconClass="text-gray-400" />
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
                     <StatCard title="Total Queries" value="135,421" subtext="+12% from last month" />
                     <StatCard title="Blocked" value="23,543" subtext="23.4% of total queries" icon="block" iconClass="text-red-500" />
                     <StatCard title="Threats" value="342" subtext="1.0% of blocked queries" icon="warning" iconClass="text-yellow-500" />
                     <StatCard title="Encrypted" value="98.7%" subtext="Of total queries" icon="enhanced_encryption" iconClass="text-green-500" />
                </div>

                <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
                    <AnalyticsChart />
                    <div className="card">
                        <h3 className="text-lg font-semibold text-white mb-4">Recent Activity</h3>
                        <p className="text-sm text-gray-400 mb-4">Latest DNS queries</p>
                        <ul className="space-y-4">
                            {recentActivities.map((activity, index) => (
                                <ActivityItem key={index} {...activity} />
                            ))}
                        </ul>
                    </div>
                </div>
            </main>
        </div>
    );
}

export default DashboardPage;
