// src/pages/DashboardPage.jsx
import React, { useEffect, useRef } from 'react';

const DashboardPage = () => {
    const chartRef = useRef(null);
    const chartInstanceRef = useRef(null); // Grafik örneğini temizleme için saklamak üzere

    useEffect(() => {
        // Chart.js'nin CDN'den yüklendiğinden emin olun
        if (window.Chart && chartRef.current) {
            // Varsa önceki grafik örneğini yok et
            if (chartInstanceRef.current) {
                chartInstanceRef.current.destroy();
            }

            const ctx = chartRef.current.getContext('2d');
            chartInstanceRef.current = new window.Chart(ctx, {
                type: 'line',
                data: {
                    labels: ['00:00', '02:00', '04:00', '06:00', '08:00', '10:00', '12:00', '14:00', '16:00', '18:00', '20:00', '22:00'],
                    datasets: [{
                        label: 'Total Queries',
                        data: [200, 180, 150, 160, 250, 500, 700, 750, 680, 600, 500, 400],
                        borderColor: 'rgb(59, 130, 246)', // blue-500
                        backgroundColor: 'rgba(59, 130, 246, 0.1)',
                        tension: 0.4,
                        borderWidth: 2,
                        pointRadius: 0,
                        pointHoverRadius: 5,
                        fill: true,
                    }, {
                        label: 'Blocked',
                        data: [50, 40, 30, 35, 60, 120, 180, 200, 150, 130, 100, 80],
                        borderColor: 'rgb(239, 68, 68)', // red-500
                        backgroundColor: 'rgba(239, 68, 68, 0.1)',
                        tension: 0.4,
                        borderWidth: 2,
                        pointRadius: 0,
                        pointHoverRadius: 5,
                        fill: true,
                    }]
                },
                options: {
                    responsive: true,
                    maintainAspectRatio: false,
                    scales: {
                        y: {
                            beginAtZero: true,
                            ticks: {
                                color: '#9CA3AF', // text-gray-400
                                stepSize: 250,
                            },
                            grid: {
                                color: '#374151', // bg-gray-700
                                drawBorder: false,
                            }
                        },
                        x: {
                            ticks: {
                                color: '#9CA3AF' // text-gray-400
                            },
                            grid: {
                                display: false, // Hide x-axis grid lines
                            }
                        }
                    },
                    plugins: {
                        legend: {
                            display: false // Custom legend below chart
                        },
                        tooltip: {
                            mode: 'index',
                            intersect: false,
                            backgroundColor: '#1F2937', // bg-gray-800
                            titleColor: '#FFFFFF',
                            bodyColor: '#D1D5DB',
                            borderColor: '#374151',
                            borderWidth: 1,
                            padding: 10,
                            cornerRadius: 4,
                        }
                    }
                }
            });
        }

        // Temizleme fonksiyonu
        return () => {
            if (chartInstanceRef.current) {
                chartInstanceRef.current.destroy();
                chartInstanceRef.current = null;
            }
        };
    }, []); // Boş bağımlılık dizisi, bunun mount edildiğinde bir kez çalışmasını ve unmount edildiğinde temizlenmesini sağlar

    const pageStyles = `
        .dashboard-page-container { /* Bu bileşenin kök div'ine uygulanır */
            font-family: 'Inter', sans-serif;
            background-color: #111827;
            color: #D1D5DB;
        }
        .sidebar {
            background-color: #1F2937;
        }
        .card {
            background-color: #1F2937;
            border-radius: 0.5rem;
            padding: 1.5rem;
        }
        .nav-item {
            display: flex;
            align-items: center;
            padding: 0.75rem 1.25rem;
            border-radius: 0.375rem;
            transition: background-color 0.2s ease-in-out;
        }
        .nav-item:hover, .nav-item.active {
            background-color: #374151;
        }
        .nav-item.active {
            color: #FFFFFF;
        }
        .nav-item .material-icons {
            margin-right: 0.75rem;
            font-size: 1.25rem;
        }
        .progress-bar {
            background-color: #374151;
            border-radius: 9999px;
            height: 0.5rem;
            overflow: hidden;
        }
        .progress-bar-inner {
            background-color: #EF4444;
            height: 100%;
        }
        .badge {
            padding: 0.25rem 0.5rem;
            border-radius: 0.25rem;
            font-size: 0.75rem;
            font-weight: 500;
        }
        .badge-green {
            background-color: #10B981;
            color: #FFFFFF;
        }
